import { useMoralis } from "react-moralis";

const Header = () => {
    const { authenticate, logout, isAuthenticated, user } = useMoralis();

    const style = "uppercase font-bold md:text-2xl flex-1 border z-10 font-semibold rounded-lg shadow-md text-white bg-black h-14 hover:text-black hover:bg-white dm:text-8";

    return (
        <header className="z-10 flex flex-wrap p-4 bg-black gap-4">
            <button className={style} onClick={() => null}>Mint</button>
            {isAuthenticated ?
                <div className="flex">
                    <p onClick={() => alert("COPIED, but not))")} className="text-white border z-10 flex-1 p-1">{user?.get('ethAddress')}</p>
                    <button className={style} onClick={() => logout()}>{"LogOut"}</button>
                </div> :
                <button className={style + " bg-red-600 text-black"} onClick={() => authenticate()}>Connect wallet</button>
            }
            <button className={style} onClick={() => null}>Team</button>
            {/* <li>
                <Link to="/">Home</Link>
            </li>
            <li>
                <Link to="/about">About</Link>
            </li>
            <li>
                <Link to="/dashboard">Dashboard</Link>
            </li> */}
        </header>
    );
}
export default Header;
